% --- Executes on button press in pushbutton2.
function pushbutton2_Callback(hObject, eventdata, handles)
% hObject    handle to pushbutton2 (see GCBO)
% eventdata  reserved - to be defined in a future version of MATLAB
% handles    structure with handles and user data (see GUIDATA)
[chosenfile, chosenpath] = uigetfile('*.mp4', 'Select a video');
if ~ischar(chosenfile)
  return;   %user canceled dialog
end
filename = fullfile(chosenpath, chosenfile);
set(handles.edit1, 'UserData', filename);
set(handles.text4, 'String', ['Video : ', chosenfile]);

% --- Executes on button press in pushbutton3.
function pushbutton3_Callback(hObject, eventdata, handles)
% hObject    handle to pushbutton3 (see GCBO)
% eventdata  reserved - to be defined in a future version of MATLAB
% handles    structure with handles and user data (see GUIDATA)
filename = get(handles.edit1, 'UserData');
if ~exist(filename, 'file')
  warndlg( 'No video file loaded');
  return
end

if strcmp(get(handles.pushbutton3,'UserData'),'Play')
    set(handles.pushbutton3, 'String', 'Play', 'UserData', 'Pause');
else
    set(handles.pushbutton3, 'String', 'Pause', 'UserData', 'Play');
end

set(handles.pushbutton3, 'Enable', 'Off');
drawnow;
set(handles.pushbutton3, 'Enable', 'On');

try
  obj = VideoReader(filename);
catch
  warndlg( 'File named in edit box does not appear to be a usable movie file');
  return
end

ax = handles.axes1;
bar = handles.axes2;

nFrames = floor(obj.FrameRate * obj.Duration);
fprintf('# of frame %d. Frame rate %f', nFrames, obj.FrameRate)
scores = zeros(50000,1);
frameidx = 1;
pixIdx   = 1;

bar_image = 255 .* ones(32, 960, 3, 'uint8');
step = nFrames / 960;

while hasFrame(obj)
    vidFrame = readFrame(obj);
    image(vidFrame, 'Parent', ax);
    image(bar_image, 'Parent', bar);

    set(ax, 'Visible', 'off');
    set(bar, 'Visible', 'off');

    if strcmp(get(handles.text2,'String'),'IDEL')
        set(handles.edit2, 'String', '0');
    else
        set(handles.edit2, 'String', '1');
        scores(frameidx) = 1;
    end
    
    if strcmp(get(handles.edit4,'String'),'backwards')
        currentIdx = frameidx;
        
        backtime = obj.CurrentTime - 100 / obj.FrameRate;
        if backtime < 0
            backtime = 0;
        end
        obj.CurrentTime = backtime;
        frameidx = currentIdx - 100;
        if frameidx < 0
            frameidx = 1;
        end
        scores(frameidx : currentIdx) = 0 ;
        pixIdx = floor(frameidx/step);
        set(handles.edit4, 'String', 'forwards');
    end
    

    if frameidx >  step * pixIdx && pixIdx < 960 
        bar_image(:,pixIdx,:) = repmat([0,255,0], 32, 1);
        
        if pixIdx > 1
            if mean(scores((pixIdx-2) * step + 1 :  ( pixIdx-1 )* step)) > 0.5
                bar_image(:,pixIdx-1,:) = repmat([255,0,0], 32, 1);
            else
                bar_image(:,pixIdx-1,:) = repmat([0,0,255], 32, 1);
            end
        end
        pixIdx = pixIdx + 1;
    end
    
    anno_rate = sum(scores(:)==1) * 100 / nFrames;
    
    percentage = sprintf ('Annotation percentage: %.2f %%', anno_rate); 
    set(handles.text5, 'String', percentage);

       
    frameidx = frameidx + 1;
    pause(1/obj.FrameRate);
end

folderName = get(handles.edit3, 'String');

if exist( folderName, 'dir') == 0
    mkdir (folderName)
end

display (frameidx-1)
scores = scores (1:frameidx-1);

fileID = fopen([pwd, '/', folderName ,'/', obj.Name , '.txt'],'w');

fprintf(fileID,'%d\n', scores);    

fclose(fileID);
msgbox('Scores saved!');

clear obj


